mapper.select:
SELECT * FROM `lt_cs_qna` AS a
JOIN `lt_cs_cate2` AS b
ON a.cate1 = b.cate1 AND a.cate2 = b.cate2 ORDER BY `qnaNo` DESC LIMIT #{start}, 10;

SELECT * FROM `lt_cs_qna` AS a
JOIN `lt_cs_cate2` AS b
ON a.cate1 = b.cate1 AND a.cate2 = b.cate2 WHERE a.cate1= #{cate1} ORDER BY `qnaNo` DESC LIMIT #{start}, 10;

SELECT * FROM `lt_cs_qna` AS a
JOIN `lt_cs_cate2` AS b
ON a.cate1 = b.cate1 AND a.cate2 = b.cate2 WHERE `qnaNo`= #{qnaNo}
